    //     })
    // }
    //
    // handleSubmit = () => {
    //     const password = this.state.password;
    //
    //     this.props.onSubmit({
    //         password
    //     })
    // }

    render(){
        return(
            <form>
                <Typography display1>
                    Please enter the password.
                </Typography>
                <br />
                <TextField
                    label='Password'
                    value={this.state.password}
                    onChange={this.handleChange}
                />
                <Button color='primary' onClick={this.handleSubmit}>
                    Submit
                </Button>
            </form>
        )
    }
}
