import { Avatar, Typography } from '@material-ui/core';
import React, { Component } from 'react';

export default class EditForm extends Component {
  constructor(props) {
    super(props);
    this.state = { planet: this.props.planet };
  }

  render() {
    const {
      name, diameter, distanceFromSun, image, mass
    } = this.state;

    return (
      <>
        <Typography display1>
          {name}
        </Typography>
        <Avatar style={{ width: '100px', height: '100px' }} src={`img/${image}`} />
        <Typography subheading style={{ marginTop: 20 }}>
                Mass
        </Typography>
        <Typography paragraph style={{ marginTop: 20 }}>
          {mass}
          {' '}
                kg
        </Typography>
        <Typography subheading style={{ marginTop: 20 }}>
                Distance from Sun
        </Typography>
        <Typography paragraph style={{ marginTop: 20 }}>
          {distanceFromSun}
          {' '}
                AU (Astronomical Units)
        </Typography>
        <Typography subheading style={{ marginTop: 20 }}>
                Diameter
        </Typography>
        <Typography paragraph style={{ marginTop: 20 }}>
          {diameter}
        </Typography>
      </>
    );
  }
}
