import { Avatar, Typography } from '@material-ui/core';
import React, { Component } from 'react';

export default class EditForm extends Component {
  constructor(props) {
    super(props);
    this.state = { planet: this.props.planet };
  }

  render() {
      const {name, mass, distanceFromSun, diameter} = this.state;

    return (
      <>
        <Typography display1>
          {selectedPlanet.name}
        </Typography>
        <Avatar style={{ width: '100px', height: '100px' }} src={`img/${selectedPlanet.image}`} />
        <Typography subheading style={{ marginTop: 20 }}>
                Mass
        </Typography>
        <Typography paragraph style={{ marginTop: 20 }}>
          {selectedPlanet.mass}
          {' '}
                kg
        </Typography>
        <Typography subheading style={{ marginTop: 20 }}>
                Distance from Sun
        </Typography>
        <Typography paragraph style={{ marginTop: 20 }}>
          {selectedPlanet.distanceFromSun}
          {' '}
                AU (Astronomical Units)
        </Typography>
        <Typography subheading style={{ marginTop: 20 }}>
                Diameter
        </Typography>
        <Typography paragraph style={{ marginTop: 20 }}>
          {selectedPlanet.diameter}
        </Typography>
      </>
    );
  }
}
