import React, {Component, Fragment} from 'react'
import {
    TextField,
    FormControl,
    InputLabel,
    Select,
    DialogTitle,
    DialogContent,
    Dialog,
    Typography
} from "@material-ui/core";
import Button from "@material-ui/core/Button";

const styles = theme => ({
    FormControl:{
        width:500
    }
})

export default class EditForm extends Component {
    constructor(props) {
        super(props);
        this.state = { planet: this.props.selectedPlanet, open: false };
    }

    componentDidUpdate(prevProps) {
        if (this.props.selectedPlanet !== prevProps.selectedPlanet) {
            this.setState({planet: this.props.selectedPlanet});
        }
    }

    handleChange = name => ({ target : { value }}) => {
        this.setState({
            planet : {
                ...this.state.planet,
                [name] : value
            }
        })
    }

    handleSubmit = () => {
        const planet = this.state.planet;

        this.props.onSubmit({
            planet
        })

        this.setState({
            open: false,
            planet: {
                id: '',
                name: '',
                image: '',
                distanceFromSun: '',
                diameter: '',
                mass: ''
            }
        })
    }

    render(){
        const { distanceFromSun, diameter, mass, open } = this.state,
            {name} = this.props.selectedPlanet;

        return(<form>
            <Typography display1>
                Editing {name}
            </Typography>
            <br />
                    <TextField
                        label='Distance from Sun (AU)'
                        value={distanceFromSun}
                        defaultValue={distanceFromSun}
                        onChange={this.handleChange('distanceFromSun')}
                    />
                    <br />
                    <TextField
                        label='Diameter (KM)'
                        value={diameter}
                        onChange={this.handleChange('diameter')}
                    />
                    <br />
                    <TextField
                        label='Mass (x10^24KG)'
                        value={mass}
                        onChange={this.handleChange('mass')}
                    />
                    <br />
                    <Button color='primary' onClick={this.handleSubmit}>
                        Submit
                    </Button>
        </form>)
    }
}
