import React, {Component, Fragment} from 'react'
import {
    TextField,
    FormControl,
    InputLabel,
    Select,
    DialogTitle,
    DialogContent,
    Dialog,
    Typography
} from "@material-ui/core";
import Button from "@material-ui/core/Button";
import ErrorDialog from "./ErrorDialog";

const styles = theme => ({
    FormControl:{
        width:500
    }
})

export default class EditForm extends Component {
    constructor(props) {
        super(props);
        this.state = { authenticate: this.props.authenticate };
    }

    handleChange = value => {
        this.setState({
            authenticate : {
                Password: value
            }
        })
    }

    handleSubmit = () => {
        const authenticate = this.state.planet;

        this.props.onSubmit({
            authenticate
        })
    }

    render(){
        return(
            <form>
                <Typography display1>
                    Please enter the password.
                </Typography>
                <br />
                <TextField
                    label='Password'
                    value={this.state.planet.distanceFromSun}
                    onChange={this.handleChange}
                />
                <Button color='primary' onClick={this.handleSubmit}>
                    Submit
                </Button>
            </form>
        )
    }
}
